INSERT INTO SquidGameMS.games (id, created_at, description, game_duration, game_title, updated_at, created_by) VALUES (1, 1762466856588, 'Двоє фіналістів борються на майданчику, поділеному на зони у формі кальмара. Один атакує, інший захищається; щоб перемогти, треба дістатися до центру або вибити суперника.', 61, 'Кінцева гра (Кальмар)', 1762466856588, 28);
INSERT INTO SquidGameMS.games (id, created_at, description, game_duration, game_title, updated_at, created_by) VALUES (2, 1762466951395, 'Гравці рухаються до фінішу, коли лялька оголошує \'зелене світло\', і повинні миттєво зупинитися на \'червоне світло\'. Будь-який рух під час зупинки призводить до вибуття.', 17, 'Червоне світло, зелене світло', 1762466951395, 28);
INSERT INTO SquidGameMS.games (id, created_at, description, game_duration, game_title, updated_at, created_by) VALUES (3, 1762466958263, 'Гравці отримують цукрову плитку з фігуркою, яку потрібно акуратно вирізати, не зламавши її. Найменша тріщина веде до дискваліфікації.', 34, 'Цукрові фігурки (Дальгона)', 1762466958263, 28);
INSERT INTO SquidGameMS.games (id, created_at, description, game_duration, game_title, updated_at, created_by) VALUES (4, 1762466963080, 'Дві команди стоять на платформі й тягнуть канат у протилежні боки. Програє команда, яка падає з платформи.', 22, 'Перетягування канату', 1762466963080, 28);
INSERT INTO SquidGameMS.games (id, created_at, description, game_duration, game_title, updated_at, created_by) VALUES (5, 1762466968525, 'Гравці по черзі крокують по рядах скляних панелей, де лише одна з двох витримує вагу. Помилковий вибір призводить до падіння.', 47, 'Скляний міст', 1762466968525, 28);
INSERT INTO SquidGameMS.games (id, created_at, description, game_duration, game_title, updated_at, created_by) VALUES (6, 1762466971942, 'Гравці отримують по 10 кульок і повинні виграти всі кульки супротивника будь-яким способом за домовленими правилами (парне/непарне, відстань тощо).', 29, 'Кульки', 1762466971942, 28);
INSERT INTO SquidGameMS.games (id, created_at, description, game_duration, game_title, updated_at, created_by) VALUES (7, 1762466986772, 'Гравці по черзі стрибають по малюнку на землі за певним маршрутом, не наступаючи за межі ліній. Програє той, хто втрачає рівновагу або порушує правила.', 12, 'Сквирт-гопак', 1762466986772, 28);
